'use server';

/**
 * @fileOverview A supplier classification AI agent.
 *
 * - classifyNewSupplier - A function that handles the supplier classification process.
 * - ClassifyNewSupplierInput - The input type for the classifyNewSupplier function.
 * - ClassifyNewSupplierOutput - The return type for the classifyNewSupplier function.
 */

import {ai} from '@/ai/genkit';
import {z} from 'genkit';

const ClassifyNewSupplierInputSchema = z.object({
  supplierInformation: z
    .string()
    .describe('Detailed information about the supplier, including their industry, products, services, and company size.'),
});
export type ClassifyNewSupplierInput = z.infer<typeof ClassifyNewSupplierInputSchema>;

const ClassifyNewSupplierOutputSchema = z.object({
  supplierCategory: z
    .string()
    .describe('The category the supplier belongs to (e.g., Retailer, Wholesaler, Manufacturer).'),
  supplierReliabilityScore: z
    .number()
    .describe('A numerical score (1-100) indicating the supplier reliability based on the provided information.'),
  supplierRiskFactors: z
    .string()
    .describe('Identified risk factors associated with the supplier, if any, such as financial instability or negative reviews.'),
});
export type ClassifyNewSupplierOutput = z.infer<typeof ClassifyNewSupplierOutputSchema>;

export async function classifyNewSupplier(
  input: ClassifyNewSupplierInput
): Promise<ClassifyNewSupplierOutput> {
  return classifyNewSupplierFlow(input);
}

const prompt = ai.definePrompt({
  name: 'classifyNewSupplierPrompt',
  input: {schema: ClassifyNewSupplierInputSchema},
  output: {schema: ClassifyNewSupplierOutputSchema},
  prompt: `You are an expert in classifying suppliers based on their provided information.

  Analyze the following supplier information and classify the supplier into a relevant category (e.g., Retailer, Wholesaler, Manufacturer).
  Also, determine a reliability score (1-100) based on the information provided. Identify any potential risk factors associated with the supplier.

  Supplier Information: {{{supplierInformation}}}

  Ensure that the output is structured according to the provided output schema, including a category, reliability score, and risk factors (if any).`,
});

const classifyNewSupplierFlow = ai.defineFlow(
  {
    name: 'classifyNewSupplierFlow',
    inputSchema: ClassifyNewSupplierInputSchema,
    outputSchema: ClassifyNewSupplierOutputSchema,
  },
  async input => {
    const {output} = await prompt(input);
    return output!;
  }
);

'use server';
/**
 * @fileOverview This file defines a Genkit flow for summarizing supplier information.
 *
 * - summarizeSupplierInformation - A function that takes supplier details as input and returns a summarized assessment.
 * - SummarizeSupplierInformationInput - The input type for the summarizeSupplierInformation function.
 * - SummarizeSupplierInformationOutput - The return type for the summarizeSupplierInformation function.
 */

import {ai} from '@/ai/genkit';
import {z} from 'genkit';

const SummarizeSupplierInformationInputSchema = z.object({
  supplierDetails: z.string().describe('Detailed information about the supplier.'),
});
export type SummarizeSupplierInformationInput = z.infer<typeof SummarizeSupplierInformationInputSchema>;

const SummarizeSupplierInformationOutputSchema = z.object({
  summary: z.string().describe('A concise summary of the supplier, highlighting key strengths and weaknesses.'),
});
export type SummarizeSupplierInformationOutput = z.infer<typeof SummarizeSupplierInformationOutputSchema>;

export async function summarizeSupplierInformation(input: SummarizeSupplierInformationInput): Promise<SummarizeSupplierInformationOutput> {
  return summarizeSupplierInformationFlow(input);
}

const prompt = ai.definePrompt({
  name: 'summarizeSupplierInformationPrompt',
  input: {schema: SummarizeSupplierInformationInputSchema},
  output: {schema: SummarizeSupplierInformationOutputSchema},
  prompt: `You are an expert in supplier assessment. Summarize the following supplier information, highlighting key strengths and weaknesses, to help a user quickly assess their suitability:\n\nSupplier Details: {{{supplierDetails}}}`, 
});

const summarizeSupplierInformationFlow = ai.defineFlow(
  {
    name: 'summarizeSupplierInformationFlow',
    inputSchema: SummarizeSupplierInformationInputSchema,
    outputSchema: SummarizeSupplierInformationOutputSchema,
  },
  async input => {
    const {output} = await prompt(input);
    return output!;
  }
);

import { PageHero } from '@/components/layout/PageHero';
import AboutContent from '@/components/sections/about/AboutContent';
import { Section } from '@/components/ui/section';
import { CTASection } from '@/components/cta-section';
import { Metadata } from 'next';
import { WebPage, BreadcrumbList } from 'schema-dts';

export const metadata: Metadata = {
  title: 'About BZION | Nigeria\'s Most Trusted FMCG Supply Partner',
  description: 'Learn how BZION has become Nigeria\'s leading B2B foodstuff and FMCG distributor. Discover our mission, values, and the team dedicated to empowering your business with authentic products and reliable service.',
};

export default function AboutPage() {
  const breadcrumbItems = [
    { label: 'Home', href: '/' },
    { label: 'About', href: '/about' },
  ];

  const jsonLd: WebPage = {
    '@type': 'WebPage',
    name: metadata.title as string,
    description: metadata.description as string,
    url: 'https://bzion.vercel.app/about',
  };

  const breadcrumbJsonLd: BreadcrumbList = {
    '@type': 'BreadcrumbList',
    itemListElement: breadcrumbItems.map((item, index) => ({
      '@type': 'ListItem',
      position: index + 1,
      name: item.label,
      item: `https://bzion.vercel.app${item.href}`,
    })),
  };

  return (
    <>
      <script
        type="application/ld+json"
        dangerouslySetInnerHTML={{ __html: JSON.stringify(jsonLd) }}
      />
      <script
        type="application/ld+json"
        dangerouslySetInnerHTML={{ __html: JSON.stringify(breadcrumbJsonLd) }}
      />
      <main id="main-content" role="main" className="flex-grow">
        <PageHero
          title="The BZION Story"
          description="BZION is a leading Nigerian distribution partner, connecting businesses to authentic food products from trusted manufacturers and warehouse inventory. We serve bakeries, supermarkets, wholesalers, and other food businesses, providing reliable logistics, flexible sourcing options, and dedicated support to help businesses operate efficiently and grow sustainably."
          breadcrumbs={breadcrumbItems}
        />
        <div className="border-b-2 border-slate-200"></div>
        <AboutContent />
        <Section className="py-16">
          <CTASection
            title="Join the BZION Community"
            description="Whether you're a retail business, wholesaler, or institution, BZION is ready to support your success. Connect with us today and discover the difference reliable partnership makes."
            ctaText="Start Your Journey"
            ctaHref="/contact"
          />
        </Section>
      </main>
    </>
  );
}

/**
 * Error Logging API Route
 * Receives and processes error logs from clients
 */

import { NextRequest, NextResponse } from 'next/server'

export interface ErrorLogReport {
  id: string
  message: string
  stack?: string
  context?: Record<string, any>
  severity: 'info' | 'warning' | 'error' | 'critical'
  timestamp: number
  url: string
  userAgent: string
  sessionId: string
  userId?: string
  breadcrumbs: Array<{
    message: string
    timestamp: number
    category: string
    data?: Record<string, any>
  }>
  sourceMap?: {
    file: string
    line: number
    column: number
  }
  environment: string
  version: string
}

/**
 * POST /api/monitoring/errors
 * Receive error logs from clients
 */
export async function POST(request: NextRequest) {
  try {
    const errorLog: ErrorLogReport = await request.json()

    // Validate required fields
    if (!errorLog.message || !errorLog.sessionId) {
      return NextResponse.json(
        { error: 'Missing required fields' },
        { status: 400 }
      )
    }

    // Determine if error should be logged based on severity
    const shouldLog =
      errorLog.severity === 'critical' ||
      errorLog.severity === 'error' ||
      (process.env.NODE_ENV === 'development' && errorLog.severity === 'warning')

    if (shouldLog) {
      console.error(`[Error Log] [${errorLog.severity.toUpperCase()}]`, {
        id: errorLog.id,
        message: errorLog.message,
        url: errorLog.url,
        sessionId: errorLog.sessionId,
        userId: errorLog.userId,
        timestamp: new Date(errorLog.timestamp).toISOString(),
      })

      // Log detailed info for critical errors
      if (errorLog.severity === 'critical') {
        console.error('[Error Details]', {
          stack: errorLog.stack,
          context: errorLog.context,
          sourceMap: errorLog.sourceMap,
          breadcrumbs: errorLog.breadcrumbs.slice(-5), // Last 5 breadcrumbs
        })
      }
    }

    // Store error log (you can implement database storage here)
    // await storeErrorLog(errorLog)

    // Send to external error tracking service (e.g., Sentry, Rollbar)
    // await sendToErrorTrackingService(errorLog)

    // Send alerts for critical errors
    if (errorLog.severity === 'critical') {
      // await sendAlert(errorLog)
    }

    // Return success response
    return NextResponse.json(
      { success: true, id: errorLog.id },
      { status: 200 }
    )
  } catch (error) {
    console.error('[Error Logging API] Error:', error)
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    )
  }
}

