import { NextResponse } from 'next/server';
import { ProductService } from '@/services/productService';

export async function GET(
  request: Request,
  { params }: { params: Promise<{ slug: string }> }
) {
  const { slug } = await params;
  const product = ProductService.getProductBySlug(slug);

  if (!product) {
    return new NextResponse('Product not found', { status: 404 });
  }

  return NextResponse.json(product);
}

import { NextResponse } from 'next/server';
import { products } from '@/lib/data';
import { getBrands } from '@/services/brandService';

export async function GET(
  request: Request,
  { params }: { params: Promise<{ slug: string }> }
) {
  const { slug } = await params;
  const allBrands = await getBrands();
  const brand = allBrands.find((b) => b.slug === slug);

  if (!brand) {
    return new NextResponse('Brand not found', { status: 404 });
  }

  const brandProducts = products.filter((p) => p.brand === brand.name);

  return NextResponse.json(brandProducts);
}

import { NextResponse } from 'next/server';
import { products } from '@/lib/data';
import { getCategories } from '@/services/categoryService';

export async function GET(
  request: Request,
  { params }: { params: Promise<{ slug: string }> }
) {
  const { slug } = await params;
  const categories = getCategories();
  const category = categories.find((c) => c.slug === slug);

  if (!category) {
    return new NextResponse('Category not found', { status: 404 });
  }

  const categoryProducts = products.filter((p) => p.category === category.name);

  return NextResponse.json(categoryProducts);
}

import { ProductService } from "@/services/productService";
import { NextRequest, NextResponse } from "next/server";

export async function GET(req: NextRequest) {
    const { searchParams } = new URL(req.url);
    const page = parseInt(searchParams.get("page") || "1");
    const limit = parseInt(searchParams.get("limit") || "18");

    const products = ProductService.getAllProducts();
    const start = (page - 1) * limit;
    const end = page * limit;

    const paginatedProducts = products.slice(start, end);

    return NextResponse.json({
        products: paginatedProducts,
        totalPages: Math.ceil(products.length / limit),
    });
}

import { NextResponse } from 'next/server';
import { db } from '@/lib/db';

export async function GET(
  req: Request,
  { params }: { params: { quoteRequestId: string } }
) {
  try {
    const quoteRequest = await db.quoteRequest.findUnique({
      where: {
        id: params.quoteRequestId,
      },
      include: {
        product: true,
      },
    });

    if (!quoteRequest) {
      return new NextResponse('Not found', { status: 404 });
    }

    return NextResponse.json(quoteRequest);
  } catch (error) {
    console.error('[QUOTE_REQUEST_GET]', error);
    return new NextResponse('Internal error', { status: 500 });
  }
}

export async function PATCH(
  req: Request,
  { params }: { params: { quoteRequestId: string } }
) {
  try {
    const { ...values } = await req.json();

    const quoteRequest = await db.quoteRequest.update({
      where: {
        id: params.quoteRequestId,
      },
      data: {
        ...values,
      },
    });

    return NextResponse.json(quoteRequest);
  } catch (error) {
    console.error('[QUOTE_REQUEST_PATCH]', error);
    return new NextResponse('Internal error', { status: 500 });
  }
}

export async function DELETE(
  req: Request,
  { params }: { params: { quoteRequestId: string } }
) {
  try {
    const quoteRequest = await db.quoteRequest.delete({
      where: {
        id: params.quoteRequestId,
      },
    });

    return NextResponse.json(quoteRequest);
  } catch (error) {
    console.error('[QUOTE_REQUEST_DELETE]', error);
    return new NextResponse('Internal error', { status: 500 });
  }
}

import { NextResponse } from 'next/server';
import { db } from '@/lib/db';
import { sendQuoteRequestToWhatsApp, sendQuoteConfirmationToCustomer } from '@/lib/send-whatsapp';
import { z } from 'zod';

const quoteRequestSchema = z.object({
  name: z.string(),
  email: z.string().email(),
  phone: z.string(),
  company: z.string().optional(),
  message: z.string(),
  items: z.array(z.object({
    id: z.string(),
    quantity: z.number(),
    name: z.string(),
  })),
});

export async function POST(req: Request) {
  try {
    const json = await req.json();
    const {
      name,
      email,
      phone,
      company,
      message,
      items,
    } = quoteRequestSchema.parse(json);

    const totalItems = items.reduce((acc, item) => acc + item.quantity, 0);

    // Create a new user if one doesn't exist
    let user = await db.user.findUnique({
      where: { email },
    });
    if (!user) {
      user = await db.user.create({
        data: { email, name },
      });
    }

    const quoteRequest = await db.quoteRequest.create({
      data: {
        userId: user.id,
        requestor_name: name,
        requestor_email: email,
        requestor_phone: phone,
        company_name: company || null,
        delivery_address: message,
        requested_products: items as any,
        total_items: totalItems,
        quote_reference: `QR-${Date.now()}`,
      },
    });

    // Generate WhatsApp URLs (non-blocking)
    try {
      await sendQuoteRequestToWhatsApp({
        name,
        email,
        phone,
        company,
        address: message,
        items,
      });
    } catch (whatsappError) {
      console.warn('[WHATSAPP_NOTIFICATION_ERROR]', whatsappError);
      // Don't fail the request if WhatsApp notification fails
    }

    // Return response with quote details
    return NextResponse.json({
      id: quoteRequest.id,
      quote_reference: quoteRequest.quote_reference,
      message: 'Quote request submitted successfully.'
    }, { status: 201 });
  } catch (error) {
    if (error instanceof z.ZodError) {
      console.error('[QUOTE_REQUESTS_VALIDATION_ERROR]', error.errors);
      return NextResponse.json(
        { error: 'Invalid request data', details: error.errors },
        { status: 400 }
      );
    }
    console.error('[QUOTE_REQUESTS_POST]', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

export async function GET() {
  try {
    const quoteRequests = await db.quoteRequest.findMany({
      include: {
        user: true,
      },
      orderBy: {
        createdAt: 'desc',
      },
    });

    return NextResponse.json(quoteRequests);
  } catch (error) {
    console.error('[QUOTE_REQUESTS_GET]', error);
    return new NextResponse('Internal error', { status: 500 });
  }
}

