/**
 * Error Logging Service
 * Centralized error logging with automatic reporting
 */

export interface ErrorLogEntry {
  id: string
  message: string
  stack?: string
  context?: Record<string, any>
  severity: 'info' | 'warning' | 'error' | 'critical'
  timestamp: number
  url: string
  userAgent: string
  sessionId: string
  userId?: string
  breadcrumbs: Breadcrumb[]
  sourceMap?: {
    file: string
    line: number
    column: number
  }
}

export interface Breadcrumb {
  message: string
  timestamp: number
  category: string
  data?: Record<string, any>
}

export interface ErrorLogPayload extends ErrorLogEntry {
  environment: string
  version: string
}

class ErrorLogger {
  private sessionId: string
  private userId?: string
  private breadcrumbs: Breadcrumb[] = []
  private maxBreadcrumbs: number = 50
  private reportEndpoint: string = '/api/monitoring/errors'
  private environment: string = process.env.NODE_ENV || 'development'
  private version: string = process.env.NEXT_PUBLIC_VERSION || '1.0.0'
  private errorQueue: ErrorLogEntry[] = []
  private maxQueueSize: number = 20

  constructor(userId?: string) {
    this.sessionId = this.generateSessionId()
    this.userId = userId
    this.setupGlobalErrorHandlers()
  }

  /**
   * Generate unique session ID
   */
  private generateSessionId(): string {
    return `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
  }

  /**
   * Setup global error handlers
   */
  private setupGlobalErrorHandlers(): void {
    if (typeof window === 'undefined') return

    // Handle uncaught errors
    window.addEventListener('error', (event) => {
      this.captureError({
        message: event.message,
        stack: event.error?.stack,
        context: {
          filename: event.filename,
          lineno: event.lineno,
          colno: event.colno,
        },
        severity: 'error',
      })
    })

    // Handle unhandled promise rejections
    window.addEventListener('unhandledrejection', (event) => {
      this.captureError({
        message: event.reason?.message || 'Unhandled Promise Rejection',
        stack: event.reason?.stack,
        severity: 'error',
      })
    })
  }

  /**
   * Capture and log an error
   */
  captureError(error: {
    message: string
    stack?: string
    context?: Record<string, any>
    severity?: 'info' | 'warning' | 'error' | 'critical'
  }): ErrorLogEntry {
    const entry: ErrorLogEntry = {
      id: `error-${Date.now()}-${Math.random().toString(36).substr(2, 5)}`,
      message: error.message,
      stack: error.stack,
      context: error.context,
      severity: error.severity || 'error',
      timestamp: Date.now(),
      url: typeof window !== 'undefined' ? window.location.href : '',
      userAgent: typeof navigator !== 'undefined' ? navigator.userAgent : '',
      sessionId: this.sessionId,
      userId: this.userId,
      breadcrumbs: [...this.breadcrumbs],
    }

    // Parse stack trace for source mapping
    if (error.stack) {
      const match = error.stack.match(/at (.*?):(\d+):(\d+)/)
      if (match) {
        entry.sourceMap = {
          file: match[1],
          line: parseInt(match[2], 10),
          column: parseInt(match[3], 10),
        }
      }
    }

    // Add to queue
    this.errorQueue.push(entry)
    if (this.errorQueue.length > this.maxQueueSize) {
      this.errorQueue.shift()
    }

    // Send to endpoint
    this.sendToEndpoint(entry)

    // Log in development
    if (this.environment === 'development') {
      console.error('[Error Logger]', error.message, {
        stack: error.stack,
        context: error.context,
        breadcrumbs: this.breadcrumbs,
      })
    }

    return entry
  }

  /**
   * Capture an exception
   */
  captureException(error: Error, context?: Record<string, any>): ErrorLogEntry {
    return this.captureError({
      message: error.message,
      stack: error.stack,
      context,
      severity: 'error',
    })
  }

  /**
   * Capture a message
   */
  captureMessage(
    message: string,
    severity: 'info' | 'warning' | 'error' | 'critical' = 'info',
    context?: Record<string, any>
  ): ErrorLogEntry {
    return this.captureError({
      message,
      context,
      severity,
    })
  }

  /**
   * Add breadcrumb for tracking user actions
   */
  addBreadcrumb(breadcrumb: {
    message: string
    category?: string
    data?: Record<string, any>
  }): void {
    this.breadcrumbs.push({
      message: breadcrumb.message,
      timestamp: Date.now(),
      category: breadcrumb.category || 'user-action',
      data: breadcrumb.data,
    })

    // Keep only the most recent breadcrumbs
    if (this.breadcrumbs.length > this.maxBreadcrumbs) {
      this.breadcrumbs.shift()
    }
  }

  /**
   * Track user action
   */
  trackUserAction(action: string, data?: Record<string, any>): void {
    this.addBreadcrumb({
      message: action,
      category: 'user-action',
      data,
    })
  }

  /**
   * Track page navigation
   */
  trackPageNavigation(url: string, data?: Record<string, any>): void {
    this.addBreadcrumb({
      message: `Navigation to ${url}`,
      category: 'navigation',
      data: { url, ...data },
    })
  }

  /**
   * Track API call
   */
  trackApiCall(
    method: string,
    url: string,
    status?: number,
    duration?: number
  ): void {
    this.addBreadcrumb({
      message: `${method} ${url}`,
      category: 'api-call',
      data: { method, url, status, duration },
    })
  }

  /**
   * Set user context
   */
  setUser(userId: string, userData?: Record<string, any>): void {
    this.userId = userId
    this.addBreadcrumb({
      message: `User ${userId} set`,
      category: 'user',
      data: userData,
    })
  }

  /**
   * Clear user context
   */
  clearUser(): void {
    this.userId = undefined
    this.addBreadcrumb({
      message: 'User cleared',
      category: 'user',
    })
  }

  /**
   * Send error to endpoint
   */
  private sendToEndpoint(entry: ErrorLogEntry): void {
    const payload: ErrorLogPayload = {
      ...entry,
      environment: this.environment,
      version: this.version,
    }

    try {
      if (typeof navigator !== 'undefined' && navigator.sendBeacon) {
        // Use sendBeacon for reliable delivery (even on page unload)
        navigator.sendBeacon(this.reportEndpoint, JSON.stringify(payload))
        return
      }
    } catch (error) {
      // Silently fail if sendBeacon throws
    }

    // Fallback to fetch for non-critical endpoints
    if (typeof fetch !== 'undefined') {
      fetch(this.reportEndpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
        keepalive: true,
      }).catch(() => {
        // Silently fail
      })
    }
  }

  /**
   * Get all logged errors
   */
  getErrors(): ErrorLogEntry[] {
    return [...this.errorQueue]
  }

  /**
   * Clear error queue
   */
  clearErrors(): void {
    this.errorQueue = []
  }

  /**
   * Get breadcrumbs
   */
  getBreadcrumbs(): Breadcrumb[] {
    return [...this.breadcrumbs]
  }

  /**
   * Clear breadcrumbs
   */
  clearBreadcrumbs(): void {
    this.breadcrumbs = []
  }

  /**
   * Get session info
   */
  getSessionInfo(): {
    sessionId: string
    userId?: string
    environment: string
    version: string
  } {
    return {
      sessionId: this.sessionId,
      userId: this.userId,
      environment: this.environment,
      version: this.version,
    }
  }

  /**
   * Flush all pending errors
   */
  flush(): void {
    // Send any remaining errors without waiting for response
    // This is important for page unload scenarios
    for (const error of this.errorQueue) {
      this.sendToEndpoint(error)
    }
    this.errorQueue = []
  }
}

// Singleton instance
let logger: ErrorLogger | null = null

/**
 * Initialize error logger
 */
export function initializeErrorLogger(userId?: string, reportEndpoint?: string): ErrorLogger {
  if (!logger) {
    logger = new ErrorLogger(userId)
    if (reportEndpoint) {
      logger['reportEndpoint'] = reportEndpoint
    }
  }
  return logger
}

/**
 * Get error logger instance
 */
export function getErrorLogger(): ErrorLogger | null {
  return logger
}

/**
 * Quick capture error
 */
export function captureError(error: Error, context?: Record<string, any>): void {
  const instance = getErrorLogger()
  if (instance) {
    instance.captureException(error, context)
  }
}

/**
 * Quick capture message
 */
export function captureMessage(
  message: string,
  severity?: 'info' | 'warning' | 'error' | 'critical',
  context?: Record<string, any>
): void {
  const instance = getErrorLogger()
  if (instance) {
    instance.captureMessage(message, severity, context)
  }
}

/**
 * Quick add breadcrumb
 */
export function addBreadcrumb(
  message: string,
  category?: string,
  data?: Record<string, any>
): void {
  const instance = getErrorLogger()
  if (instance) {
    instance.addBreadcrumb({ message, category, data })
  }
}

/**
 * Formatters utility module
 * Common formatting functions for dates, numbers, currency, etc.
 */

/**
 * Format a date to a readable string
 * @param date - Date object or date string
 * @param locale - Locale string (default: 'en-US')
 * @returns Formatted date string
 */
export function formatDate(date: Date | string, locale: string = 'en-US'): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date
  return dateObj.toLocaleDateString(locale, {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  })
}

/**
 * Format a date with time
 * @param date - Date object or date string
 * @param locale - Locale string (default: 'en-US')
 * @returns Formatted date and time string
 */
export function formatDateTime(date: Date | string, locale: string = 'en-US'): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date
  return dateObj.toLocaleDateString(locale, {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
  })
}

/**
 * Format a number as currency
 * @param amount - Number to format
 * @param currency - Currency code (default: 'USD')
 * @param locale - Locale string (default: 'en-US')
 * @returns Formatted currency string
 */
export function formatCurrency(
  amount: number,
  currency: string = 'USD',
  locale: string = 'en-US'
): string {
  return new Intl.NumberFormat(locale, {
    style: 'currency',
    currency,
  }).format(amount)
}

/**
 * Format a number with thousand separators
 * @param number - Number to format
 * @param decimals - Number of decimal places (default: 0)
 * @returns Formatted number string
 */
export function formatNumber(number: number, decimals: number = 0): string {
  return new Intl.NumberFormat('en-US', {
    minimumFractionDigits: decimals,
    maximumFractionDigits: decimals,
  }).format(number)
}

/**
 * Format bytes to human readable format
 * @param bytes - Number of bytes
 * @param decimals - Number of decimal places (default: 2)
 * @returns Formatted bytes string
 */
export function formatBytes(bytes: number, decimals: number = 2): string {
  if (bytes === 0) return '0 Bytes'

  const k = 1024
  const dm = decimals < 0 ? 0 : decimals
  const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB']
  const i = Math.floor(Math.log(bytes) / Math.log(k))

  return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i]
}

/**
 * Format a string to title case
 * @param str - String to format
 * @returns Title case string
 */
export function formatTitleCase(str: string): string {
  return str
    .toLowerCase()
    .split(' ')
    .map(word => word.charAt(0).toUpperCase() + word.slice(1))
    .join(' ')
}

/**
 * Format a string to sentence case
 * @param str - String to format
 * @returns Sentence case string
 */
export function formatSentenceCase(str: string): string {
  return str.charAt(0).toUpperCase() + str.slice(1).toLowerCase()
}

/**
 * Slug a string (convert to URL-friendly format)
 * @param str - String to slug
 * @returns Slugified string
 */
export function formatSlug(str: string): string {
  return str
    .toLowerCase()
    .trim()
    .replace(/[^\w\s-]/g, '')
    .replace(/[\s_]+/g, '-')
    .replace(/^-+|-+$/g, '')
}

/**
 * Truncate a string to a maximum length with ellipsis
 * @param str - String to truncate
 * @param maxLength - Maximum length
 * @param ellipsis - Ellipsis string (default: '...')
 * @returns Truncated string
 */
export function formatTruncate(str: string, maxLength: number, ellipsis: string = '...'): string {
  if (str.length <= maxLength) return str
  return str.slice(0, maxLength - ellipsis.length) + ellipsis
}

/**
 * Format phone number
 * @param phoneNumber - Phone number string (numbers only)
 * @returns Formatted phone number (e.g., (123) 456-7890)
 */
export function formatPhoneNumber(phoneNumber: string): string {
  const cleaned = phoneNumber.replace(/\D/g, '')
  if (cleaned.length !== 10) return phoneNumber

  return `(${cleaned.slice(0, 3)}) ${cleaned.slice(3, 6)}-${cleaned.slice(6)}`
}

/**
 * Format a relative time (e.g., "2 hours ago")
 * @param date - Date object or date string
 * @returns Relative time string
 */
export function formatRelativeTime(date: Date | string): string {
  const dateObj = typeof date === 'string' ? new Date(date) : date
  const now = new Date()
  const seconds = Math.floor((now.getTime() - dateObj.getTime()) / 1000)

  const intervals: { [key: string]: number } = {
    year: 31536000,
    month: 2592000,
    week: 604800,
    day: 86400,
    hour: 3600,
    minute: 60,
  }

  for (const [key, value] of Object.entries(intervals)) {
    const interval = Math.floor(seconds / value)
    if (interval >= 1) {
      return `${interval} ${key}${interval > 1 ? 's' : ''} ago`
    }
  }

  return 'just now'
}

/**
 * Performance Monitoring Utilities
 * Custom metrics tracking and performance measurement
 */

export interface PerformanceMetric {
  name: string
  value: number
  unit: 'ms' | 'bytes' | 'count' | 'percent'
  tags?: Record<string, string>
  timestamp: number
}

export interface PerformanceThresholds {
  name: string
  good: number
  warning: number
  critical: number
  unit: string
}

class PerformanceMonitor {
  private metrics: Map<string, PerformanceMetric[]> = new Map()
  private marks: Map<string, number> = new Map()
  private reportEndpoint: string = '/api/monitoring/metrics'
  private sessionId: string

  constructor() {
    this.sessionId = this.generateSessionId()
  }

  /**
   * Generate unique session ID
   */
  private generateSessionId(): string {
    return `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
  }

  /**
   * Start timing measurement
   */
  startMeasurement(name: string): void {
    this.marks.set(name, performance.now())
  }

  /**
   * End timing measurement and record metric
   */
  endMeasurement(
    name: string,
    tags?: Record<string, string>
  ): PerformanceMetric | undefined {
    const startTime = this.marks.get(name)
    if (!startTime) {
      console.warn(`No start mark found for measurement: ${name}`)
      return undefined
    }

    const duration = performance.now() - startTime
    const metric: PerformanceMetric = {
      name,
      value: duration,
      unit: 'ms',
      tags,
      timestamp: Date.now(),
    }

    this.recordMetric(metric)
    this.marks.delete(name)

    return metric
  }

  /**
   * Record custom metric
   */
  recordMetric(metric: PerformanceMetric): void {
    if (!this.metrics.has(metric.name)) {
      this.metrics.set(metric.name, [])
    }

    const metricList = this.metrics.get(metric.name)!
    metricList.push(metric)

    // Keep only recent metrics (last 100)
    if (metricList.length > 100) {
      metricList.shift()
    }

    if (process.env.NODE_ENV === 'development') {
      console.log(`[Performance] ${metric.name}: ${metric.value.toFixed(2)}${metric.unit}`, {
        tags: metric.tags,
      })
    }
  }

  /**
   * Measure function execution time
   */
  async measureAsync<T>(
    name: string,
    fn: () => Promise<T>,
    tags?: Record<string, string>
  ): Promise<T> {
    this.startMeasurement(name)
    try {
      return await fn()
    } finally {
      this.endMeasurement(name, tags)
    }
  }

  /**
   * Measure synchronous function execution time
   */
  measure<T>(
    name: string,
    fn: () => T,
    tags?: Record<string, string>
  ): T {
    this.startMeasurement(name)
    try {
      return fn()
    } finally {
      this.endMeasurement(name, tags)
    }
  }

  /**
   * Get metric statistics
   */
  getMetricStats(name: string): {
    count: number
    min: number
    max: number
    avg: number
    p95: number
    p99: number
  } | null {
    const metrics = this.metrics.get(name)
    if (!metrics || metrics.length === 0) {
      return null
    }

    const values = metrics.map((m) => m.value).sort((a, b) => a - b)
    const sum = values.reduce((a, b) => a + b, 0)

    return {
      count: values.length,
      min: values[0],
      max: values[values.length - 1],
      avg: sum / values.length,
      p95: values[Math.floor(values.length * 0.95)],
      p99: values[Math.floor(values.length * 0.99)],
    }
  }

  /**
   * Track memory usage
   */
  trackMemoryUsage(): {
    usedJSHeapSize: number
    totalJSHeapSize: number
    jsHeapSizeLimit: number
    percentUsed: number
  } | null {
    if (!(performance as any).memory) {
      return null
    }

    const memory = (performance as any).memory
    const percentUsed = (memory.usedJSHeapSize / memory.jsHeapSizeLimit) * 100

    const metric: PerformanceMetric = {
      name: 'memory_usage',
      value: percentUsed,
      unit: 'percent',
      timestamp: Date.now(),
    }

    this.recordMetric(metric)

    return {
      usedJSHeapSize: memory.usedJSHeapSize,
      totalJSHeapSize: memory.totalJSHeapSize,
      jsHeapSizeLimit: memory.jsHeapSizeLimit,
      percentUsed,
    }
  }

  /**
   * Track long tasks
   */
  trackLongTasks(): void {
    if (!('PerformanceObserver' in window)) return

    try {
      const observer = new PerformanceObserver((list) => {
        for (const entry of list.getEntries()) {
          const metric: PerformanceMetric = {
            name: 'long_task',
            value: entry.duration,
            unit: 'ms',
            tags: { name: entry.name },
            timestamp: Date.now(),
          }

          this.recordMetric(metric)
        }
      })

      observer.observe({ entryTypes: ['longtask'] })
    } catch (e) {
      // Long task observer not supported
    }
  }

  /**
   * Track resource timing
   */
  getResourceMetrics(): {
    totalResources: number
    totalSize: number
    averageLoadTime: number
    slowestResources: Array<{ name: string; duration: number; size: number }>
  } {
    const resources = performance.getEntriesByType('resource') as PerformanceResourceTiming[]

    const totalSize = resources.reduce((sum, r) => sum + (r.transferSize || 0), 0)
    const totalDuration = resources.reduce((sum, r) => sum + r.duration, 0)

    const slowest = resources
      .sort((a, b) => b.duration - a.duration)
      .slice(0, 10)
      .map((r) => ({
        name: r.name.split('/').pop() || r.name,
        duration: r.duration,
        size: r.transferSize || 0,
      }))

    return {
      totalResources: resources.length,
      totalSize,
      averageLoadTime: totalDuration / resources.length,
      slowestResources: slowest,
    }
  }

  /**
   * Get all metrics
   */
  getAllMetrics(): Map<string, PerformanceMetric[]> {
    return new Map(this.metrics)
  }

  /**
   * Get metrics as array
   */
  getMetricsArray(): PerformanceMetric[] {
    const all: PerformanceMetric[] = []
    for (const metrics of this.metrics.values()) {
      all.push(...metrics)
    }
    return all.sort((a, b) => a.timestamp - b.timestamp)
  }

  /**
   * Clear metrics
   */
  clearMetrics(): void {
    this.metrics.clear()
    this.marks.clear()
  }

  /**
   * Report metrics to endpoint
   */
  async reportMetrics(): Promise<void> {
    const metrics = this.getMetricsArray()
    if (metrics.length === 0) return

    try {
      await fetch(this.reportEndpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          sessionId: this.sessionId,
          metrics,
          timestamp: Date.now(),
        }),
        keepalive: true,
      })
    } catch (e) {
      // Silently fail
    }
  }

  /**
   * Get session ID
   */
  getSessionId(): string {
    return this.sessionId
  }
}

// Singleton instance
let monitor: PerformanceMonitor | null = null

/**
 * Initialize performance monitor
 */
export function initializePerformanceMonitor(): PerformanceMonitor {
  if (!monitor) {
    monitor = new PerformanceMonitor()
  }
  return monitor
}

/**
 * Get performance monitor instance
 */
export function getPerformanceMonitor(): PerformanceMonitor | null {
  return monitor
}

/**
 * Quick start measurement
 */
export function startMeasurement(name: string): void {
  const instance = getPerformanceMonitor()
  if (instance) {
    instance.startMeasurement(name)
  }
}

/**
 * Quick end measurement
 */
export function endMeasurement(name: string, tags?: Record<string, string>): void {
  const instance = getPerformanceMonitor()
  if (instance) {
    instance.endMeasurement(name, tags)
  }
}

/**
 * Measure async function
 */
export async function measureAsync<T>(
  name: string,
  fn: () => Promise<T>,
  tags?: Record<string, string>
): Promise<T> {
  const instance = getPerformanceMonitor()
  if (instance) {
    return instance.measureAsync(name, fn, tags)
  }
  return fn()
}

/**
 * Measure sync function
 */
export function measure<T>(
  name: string,
  fn: () => T,
  tags?: Record<string, string>
): T {
  const instance = getPerformanceMonitor()
  if (instance) {
    return instance.measure(name, fn, tags)
  }
  return fn()
}

