import { clsx, type ClassValue } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

/**
 * Validators utility module
 * Common validation functions for forms and data
 */

/**
 * Validate email format
 * @param email - Email string to validate
 * @returns true if valid email format
 */
export function validateEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/
  return emailRegex.test(email)
}

/**
 * Validate password strength
 * @param password - Password string to validate
 * @param minLength - Minimum password length (default: 8)
 * @returns true if password meets requirements
 */
export function validatePassword(password: string, minLength: number = 8): boolean {
  // At least minLength characters, one uppercase, one lowercase, one number, one special char
  const regex = new RegExp(
    `^(?=.*[a-z])(?=.*[A-Z])(?=.*\\d)(?=.*[@$!%*?&])[A-Za-z\\d@$!%*?&]{${minLength},}$`
  )
  return regex.test(password)
}

/**
 * Validate URL format
 * @param url - URL string to validate
 * @returns true if valid URL format
 */
export function validateUrl(url: string): boolean {
  try {
    new URL(url)
    return true
  } catch {
    return false
  }
}

/**
 * Validate phone number (10 digit US format)
 * @param phoneNumber - Phone number string
 * @returns true if valid phone number
 */
export function validatePhoneNumber(phoneNumber: string): boolean {
  const cleaned = phoneNumber.replace(/\D/g, '')
  return cleaned.length === 10
}

/**
 * Validate number is within range
 * @param value - Number to validate
 * @param min - Minimum value (inclusive)
 * @param max - Maximum value (inclusive)
 * @returns true if number is within range
 */
export function validateRange(value: number, min: number, max: number): boolean {
  return value >= min && value <= max
}

/**
 * Validate string length
 * @param str - String to validate
 * @param minLength - Minimum length
 * @param maxLength - Maximum length (optional)
 * @returns true if string length is valid
 */
export function validateLength(str: string, minLength: number, maxLength?: number): boolean {
  const len = str.length
  if (len < minLength) return false
  if (maxLength !== undefined && len > maxLength) return false
  return true
}

/**
 * Validate that a string is not empty/whitespace
 * @param str - String to validate
 * @returns true if string is not empty
 */
export function validateRequired(str: string): boolean {
  return str.trim().length > 0
}

/**
 * Validate that a value matches a pattern
 * @param value - Value to validate
 * @param pattern - Regex pattern to match
 * @returns true if value matches pattern
 */
export function validatePattern(value: string, pattern: RegExp): boolean {
  return pattern.test(value)
}

/**
 * Validate credit card number (basic Luhn algorithm)
 * @param cardNumber - Credit card number string (numbers only)
 * @returns true if card number passes Luhn check
 */
export function validateCreditCard(cardNumber: string): boolean {
  const cleaned = cardNumber.replace(/\D/g, '')

  if (cleaned.length < 13 || cleaned.length > 19) return false

  let sum = 0
  let isEven = false

  for (let i = cleaned.length - 1; i >= 0; i--) {
    let digit = parseInt(cleaned[i], 10)

    if (isEven) {
      digit *= 2
      if (digit > 9) {
        digit -= 9
      }
    }

    sum += digit
    isEven = !isEven
  }

  return sum % 10 === 0
}

/**
 * Validate username (alphanumeric and underscores, 3-20 chars)
 * @param username - Username string to validate
 * @returns true if valid username format
 */
export function validateUsername(username: string): boolean {
  const regex = /^[a-zA-Z0-9_]{3,20}$/
  return regex.test(username)
}

/**
 * Validate date string (YYYY-MM-DD format)
 * @param dateStr - Date string to validate
 * @returns true if valid date format and real date
 */
export function validateDate(dateStr: string): boolean {
  const regex = /^\d{4}-\d{2}-\d{2}$/
  if (!regex.test(dateStr)) return false

  const date = new Date(dateStr)
  return date instanceof Date && !isNaN(date.getTime())
}

/**
 * Validate IPv4 address
 * @param ip - IP address string to validate
 * @returns true if valid IPv4 address
 */
export function validateIPv4(ip: string): boolean {
  const regex = /^(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/
  return regex.test(ip)
}

/**
 * Validate that two values match (useful for password confirmation)
 * @param value - First value
 * @param confirmValue - Second value to match
 * @returns true if values match
 */
export function validateMatch(value: string, confirmValue: string): boolean {
  return value === confirmValue
}

/**
 * Validate hex color
 * @param color - Hex color string
 * @returns true if valid hex color
 */
export function validateHexColor(color: string): boolean {
  const regex = /^#(?:[0-9a-fA-F]{3}){1,2}$/
  return regex.test(color)
}

/**
 * Web Vitals Tracking
 * Tracks Core Web Vitals metrics: LCP, FID, CLS, TTFB, FCP
 * Sends metrics to monitoring endpoint
 */

export interface WebVitalMetric {
  name: string
  value: number
  rating: 'good' | 'needs-improvement' | 'poor'
  delta: number
  id: string
  navigationType: string
  entries: PerformanceEntry[]
}

export interface WebVitalPayload {
  name: string
  value: number
  rating: 'good' | 'needs-improvement' | 'poor'
  delta: number
  id: string
  url: string
  timestamp: number
  userAgent: string
  sessionId: string
}

class WebVitalsTracker {
  private sessionId: string
  private metrics: Map<string, WebVitalPayload> = new Map()
  private reportEndpoint: string = '/api/monitoring/web-vitals'

  constructor() {
    this.sessionId = this.generateSessionId()
  }

  /**
   * Generate unique session ID
   */
  private generateSessionId(): string {
    return `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
  }

  /**
   * Determine Web Vital rating based on thresholds
   */
  private getRating(
    metricName: string,
    value: number
  ): 'good' | 'needs-improvement' | 'poor' {
    // Based on Google's Web Vitals thresholds
    const thresholds: { [key: string]: { good: number; needsImprovement: number } } = {
      LCP: { good: 2500, needsImprovement: 4000 }, // Largest Contentful Paint
      FID: { good: 100, needsImprovement: 300 }, // First Input Delay
      CLS: { good: 0.1, needsImprovement: 0.25 }, // Cumulative Layout Shift
      TTFB: { good: 600, needsImprovement: 1200 }, // Time to First Byte
      FCP: { good: 1800, needsImprovement: 3000 }, // First Contentful Paint
    }

    const threshold = thresholds[metricName]
    if (!threshold) return 'good'

    if (value <= threshold.good) return 'good'
    if (value <= threshold.needsImprovement) return 'needs-improvement'
    return 'poor'
  }

  /**
   * Report a Web Vital metric
   */
  private reportMetric(metric: WebVitalMetric): void {
    const payload: WebVitalPayload = {
      name: metric.name,
      value: metric.value,
      rating: metric.rating,
      delta: metric.delta,
      id: metric.id,
      url: typeof window !== 'undefined' ? window.location.href : '',
      timestamp: Date.now(),
      userAgent: typeof navigator !== 'undefined' ? navigator.userAgent : '',
      sessionId: this.sessionId,
    }

    // Store locally
    this.metrics.set(metric.name, payload)

    // Send to monitoring endpoint
    this.sendToEndpoint(payload)

    // Log in development
    if (process.env.NODE_ENV === 'development') {
      console.log(`[Web Vitals] ${metric.name}:`, {
        value: `${metric.value.toFixed(2)}ms`,
        rating: metric.rating,
        delta: `${metric.delta.toFixed(2)}ms`,
      })
    }
  }

  /**
   * Send metric to monitoring endpoint
   */
  private sendToEndpoint(payload: WebVitalPayload): void {
    if (!navigator.sendBeacon) {
      // Fallback to fetch if sendBeacon not available
      fetch(this.reportEndpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
        keepalive: true,
      }).catch(() => {
        // Silently fail
      })
      return
    }

    // Use sendBeacon for reliable delivery even if page unloads
    navigator.sendBeacon(this.reportEndpoint, JSON.stringify(payload))
  }

  /**
   * Track Largest Contentful Paint (LCP)
   */
  trackLCP(): void {
    if (!('PerformanceObserver' in window)) return

    let lastEntryTime = 0
    let sessionEntryTime = 0
    let sessionValue = 0

    const observer = new PerformanceObserver((list) => {
      const entries = list.getEntries()
      const lastEntry = entries[entries.length - 1] as PerformanceEntryWithStartTime

      sessionValue = lastEntry.startTime
      lastEntryTime = sessionValue
      sessionEntryTime = sessionValue

      // Ignore entries that start after user interaction
      if (lastEntry.startTime < this.getFirstInputOrPointerDown()) {
        return
      }

      const metric: WebVitalMetric = {
        name: 'LCP',
        value: sessionValue,
        rating: this.getRating('LCP', sessionValue),
        delta: sessionValue - (lastEntryTime || 0),
        id: `lcp-${Date.now()}`,
        navigationType: this.getNavigationType(),
        entries: entries,
      }

      this.reportMetric(metric)
    })

    try {
      observer.observe({ entryTypes: ['largest-contentful-paint'] })
    } catch (e) {
      // Observer not supported
    }
  }

  /**
   * Track First Input Delay (FID)
   */
  trackFID(): void {
    if (!('PerformanceObserver' in window)) return

    const observer = new PerformanceObserver((list) => {
      const entries = list.getEntries()
      const firstEntry = entries[0] as PerformanceEventTiming

      if (!firstEntry.processingStart) {
        return
      }

      const metric: WebVitalMetric = {
        name: 'FID',
        value: firstEntry.processingStart - firstEntry.startTime,
        rating: this.getRating('FID', firstEntry.processingStart - firstEntry.startTime),
        delta: 0,
        id: `fid-${Date.now()}`,
        navigationType: this.getNavigationType(),
        entries: entries,
      }

      this.reportMetric(metric)
      observer.disconnect()
    })

    try {
      observer.observe({ entryTypes: ['first-input'] })
    } catch (e) {
      // Observer not supported
    }
  }

  /**
   * Track Cumulative Layout Shift (CLS)
   */
  trackCLS(): void {
    if (!('PerformanceObserver' in window)) return

    let clsValue = 0
    let clsEntries: PerformanceEntry[] = []

    const observer = new PerformanceObserver((list) => {
      for (const entry of list.getEntries()) {
        const layoutShiftEntry = entry as any
        if (!layoutShiftEntry.hadRecentInput) {
          clsValue += layoutShiftEntry.value
          clsEntries.push(entry)
        }
      }

      const metric: WebVitalMetric = {
        name: 'CLS',
        value: clsValue,
        rating: this.getRating('CLS', clsValue),
        delta: 0,
        id: `cls-${Date.now()}`,
        navigationType: this.getNavigationType(),
        entries: clsEntries,
      }

      this.reportMetric(metric)
    })

    try {
      observer.observe({ type: 'layout-shift', buffered: true })
    } catch (e) {
      // Observer not supported
    }
  }

  /**
   * Track Time to First Byte (TTFB)
   */
  trackTTFB(): void {
    if (!('performance' in window)) return

    const navigation = performance.getEntriesByType('navigation')[0] as PerformanceNavigationTiming
    if (!navigation) return

    const ttfb = navigation.responseStart - navigation.fetchStart

    const metric: WebVitalMetric = {
      name: 'TTFB',
      value: ttfb,
      rating: this.getRating('TTFB', ttfb),
      delta: 0,
      id: `ttfb-${Date.now()}`,
      navigationType: this.getNavigationType(),
      entries: [navigation],
    }

    this.reportMetric(metric)
  }

  /**
   * Track First Contentful Paint (FCP)
   */
  trackFCP(): void {
    if (!('PerformanceObserver' in window)) return

    const observer = new PerformanceObserver((list) => {
      const entries = list.getEntries()
      const lastEntry = entries[entries.length - 1] as PerformanceEntryWithStartTime

      const metric: WebVitalMetric = {
        name: 'FCP',
        value: lastEntry.startTime,
        rating: this.getRating('FCP', lastEntry.startTime),
        delta: 0,
        id: `fcp-${Date.now()}`,
        navigationType: this.getNavigationType(),
        entries: entries,
      }

      this.reportMetric(metric)
    })

    try {
      observer.observe({ entryTypes: ['paint'] })
    } catch (e) {
      // Observer not supported
    }
  }

  /**
   * Initialize all Web Vitals tracking
   */
  initializeTracking(): void {
    if (typeof window === 'undefined') return

    // Use requestIdleCallback if available, otherwise use setTimeout
    if ('requestIdleCallback' in window) {
      requestIdleCallback(() => {
        this.trackLCP()
        this.trackFID()
        this.trackCLS()
        this.trackTTFB()
        this.trackFCP()
      })
    } else {
      setTimeout(() => {
        this.trackLCP()
        this.trackFID()
        this.trackCLS()
        this.trackTTFB()
        this.trackFCP()
      }, 0)
    }
  }

  /**
   * Get first input or pointer down time
   */
  private getFirstInputOrPointerDown(): number {
    if ('PerformanceObserver' in window) {
      try {
        const observer = new PerformanceObserver((list) => {
          const firstEntry = list.getEntries()[0]
          return firstEntry.startTime
        })
        observer.observe({ entryTypes: ['first-input', 'pointerdown'], buffered: true })
      } catch (e) {
        return Infinity
      }
    }
    return Infinity
  }

  /**
   * Get navigation type
   */
  private getNavigationType(): string {
    if ('navigation' in performance) {
      const navigation = performance.getEntriesByType('navigation')[0] as any
      return navigation?.type || 'navigate'
    }
    return 'navigate'
  }

  /**
   * Get all collected metrics
   */
  getMetrics(): WebVitalPayload[] {
    return Array.from(this.metrics.values())
  }

  /**
   * Get specific metric
   */
  getMetric(name: string): WebVitalPayload | undefined {
    return this.metrics.get(name)
  }

  /**
   * Clear metrics
   */
  clearMetrics(): void {
    this.metrics.clear()
  }
}

// Singleton instance
let tracker: WebVitalsTracker | null = null

/**
 * Initialize Web Vitals tracking
 */
export function initializeWebVitals(reportEndpoint?: string): WebVitalsTracker {
  if (!tracker) {
    tracker = new WebVitalsTracker()
    if (reportEndpoint) {
      tracker['reportEndpoint'] = reportEndpoint
    }
    tracker.initializeTracking()
  }
  return tracker
}

/**
 * Get Web Vitals tracker instance
 */
export function getWebVitalsTracker(): WebVitalsTracker | null {
  return tracker
}

