export function getResourceItems(): ResourceItem[] {
  return resourceItems;
}

export function getResourceItemBySlug(slug: string): ResourceItem | undefined {
  return resourceItems.find(item => item.slug === slug);
}

export function getResourcesByCategory(category: ResourceItem['category']): ResourceItem[] {
  return resourceItems.filter(item => item.category === category);
}

export function getCategories(): ResourceItem['category'][] {
  const categories = new Set(resourceItems.map(item => item.category));
  return Array.from(categories) as ResourceItem['category'][];
}

import { getAllProducts } from './productService';

export interface SearchResult {
  id: string;
  name: string;
  slug: string;
  brand: string;
  category: string;
  price: number;
  imageUrl: string;
  excerpt: string;
}

/**
 * Search products by query string
 * Searches in name, brand, category, and description
 */
export async function searchProducts(query: string, minChars: number = 3): Promise<SearchResult[]> {
  if (query.length < minChars) {
    return [];
  }

  const lowerQuery = query.toLowerCase().trim();
  const products = await getAllProducts();

  return products
    .filter((product) => {
      const searchableText = `
          ${product.name}
          ${product.brand}
          ${product.category}
          ${product.description}
        `.toLowerCase();

      return searchableText.includes(lowerQuery);
    })
    .map((product) => ({
      id: String(product.id),
      name: product.name,
      slug: product.slug,
      brand: product.brand,
      category: product.category,
      price: product.price,
      imageUrl: product.imageUrl,
      excerpt: product.description.substring(0, 100),
    }))
    .slice(0, 10); // Return top 10 results
}

/**
 * Get suggestions based on partial query
 */
export async function getSuggestions(query: string, limit: number = 5): Promise<string[]> {
  if (query.length < 2) {
    return [];
  }

  const lowerQuery = query.toLowerCase().trim();
  const uniqueSuggestions = new Set<string>();
  const products = await getAllProducts();

  // Add product names
  products.forEach((product) => {
    if (
      product.name.toLowerCase().includes(lowerQuery) &&
      uniqueSuggestions.size < limit
    ) {
      uniqueSuggestions.add(product.name);
    }
  });

  // Add brands
  if (uniqueSuggestions.size < limit) {
    products.forEach((product) => {
      if (
        product.brand.toLowerCase().includes(lowerQuery) &&
        !uniqueSuggestions.has(product.brand) &&
        uniqueSuggestions.size < limit
      ) {
        uniqueSuggestions.add(product.brand);
      }
    });
  }

  // Add categories
  if (uniqueSuggestions.size < limit) {
    products.forEach((product) => {
      if (
        product.category.toLowerCase().includes(lowerQuery) &&
        !uniqueSuggestions.has(product.category) &&
        uniqueSuggestions.size < limit
      ) {
        uniqueSuggestions.add(product.category);
      }
    });
  }

  return Array.from(uniqueSuggestions);
}

import { create } from 'zustand';
import { persist } from 'zustand/middleware';
import type { Product } from '@/lib/schema';

export interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
  imageUrl: string;
  brand: string;
}

interface CartStore {
  items: CartItem[];
  addItem: (product: Product, quantity: number) => void;
  removeItem: (productId: number) => void;
  updateQuantity: (productId: number, quantity: number) => void;
  clearCart: () => void;
  getTotal: () => number;
  getItemCount: () => number;
}

/**
 * Cart Store
 * Manages shopping cart state with localStorage persistence
 */
export const useCartStore = create<CartStore>()(
  persist(
    (set, get) => ({
      items: [],

      addItem: (product: Product, quantity: number) => {
        set((state) => {
          const existingItem = state.items.find((item) => item.id === product.id);

          if (existingItem) {
            return {
              items: state.items.map((item) =>
                item.id === product.id
                  ? { ...item, quantity: item.quantity + quantity }
                  : item
              ),
            };
          }

          return {
            items: [
              ...state.items,
              {
                id: product.id,
                name: product.name,
                price: product.price || 0,
                quantity,
                imageUrl: product.imageUrl || '',
                brand: product.brand || '',
              },
            ],
          };
        });
      },

      removeItem: (productId: number) => {
        set((state) => ({
          items: state.items.filter((item) => item.id !== productId),
        }));
      },

      updateQuantity: (productId: number, quantity: number) => {
        if (quantity <= 0) {
          get().removeItem(productId);
          return;
        }

        set((state) => ({
          items: state.items.map((item) =>
            item.id === productId ? { ...item, quantity } : item
          ),
        }));
      },

      clearCart: () => set({ items: [] }),

      getTotal: () => {
        return get().items.reduce((total, item) => total + item.price * item.quantity, 0);
      },

      getItemCount: () => {
        return get().items.reduce((count, item) => count + item.quantity, 0);
      },
    }),
    {
      name: 'cart-storage',
      partialize: (state) => ({ items: state.items }),
    }
  )
);

import { create } from 'zustand';
import { persist } from 'zustand/middleware';

interface MenuStore {
  isMenuOpen: boolean;
  isSubmenuOpen: { [key: string]: boolean };
  toggleMenu: () => void;
  closeMenu: () => void;
  openMenu: () => void;
  toggleSubmenu: (key: string) => void;
  closeSubmenu: (key: string) => void;
  closeAllSubmenus: () => void;
}

/**
 * Mobile Menu Store
 * Manages global mobile menu state with submenu support
 */
export const useMenuStore = create<MenuStore>((set) => ({
  isMenuOpen: false,
  isSubmenuOpen: {},

  toggleMenu: () => set((state) => ({ isMenuOpen: !state.isMenuOpen })),

  closeMenu: () => set({ isMenuOpen: false, isSubmenuOpen: {} }),

  openMenu: () => set({ isMenuOpen: true }),

  toggleSubmenu: (key: string) => 
    set((state) => ({
      isSubmenuOpen: {
        ...state.isSubmenuOpen,
        [key]: !state.isSubmenuOpen[key],
      },
    })),

  closeSubmenu: (key: string) =>
    set((state) => ({
      isSubmenuOpen: {
        ...state.isSubmenuOpen,
        [key]: false,
      },
    })),

  closeAllSubmenus: () => set({ isSubmenuOpen: {} }),
}));

import { create } from 'zustand';
import { persist } from 'zustand/middleware';

interface UserPreferences {
  theme: 'light' | 'dark' | 'system';
  language: string;
  notifications: boolean;
  compactMode: boolean;
}

interface PreferencesStore extends UserPreferences {
  setTheme: (theme: 'light' | 'dark' | 'system') => void;
  setLanguage: (language: string) => void;
  setNotifications: (enabled: boolean) => void;
  setCompactMode: (enabled: boolean) => void;
  resetPreferences: () => void;
}

const defaultPreferences: UserPreferences = {
  theme: 'system',
  language: 'en',
  notifications: true,
  compactMode: false,
};

/**
 * User Preferences Store
 * Manages user preferences with localStorage persistence
 */
export const usePreferencesStore = create<PreferencesStore>()(
  persist(
    (set) => ({
      ...defaultPreferences,

      setTheme: (theme: 'light' | 'dark' | 'system') => {
        set({ theme });
        // Apply theme to document
        if (theme !== 'system') {
          document.documentElement.classList.toggle('dark', theme === 'dark');
        }
      },

      setLanguage: (language: string) => set({ language }),

      setNotifications: (notifications: boolean) => set({ notifications }),

      setCompactMode: (compactMode: boolean) => set({ compactMode }),

      resetPreferences: () => set(defaultPreferences),
    }),
    {
      name: 'user-preferences',
    }
  )
);

import { create } from 'zustand';

export interface UIStore {
  // Navigation
  isMobileMenuOpen: boolean;
  activeDropdown: string | null;

  // Carousel
  activeSlide: number;
  isAutoPlaying: boolean;

  // Search
  searchQuery: string;
  isSearchOpen: boolean;

  // Actions
  toggleMobileMenu: () => void;
  setActiveDropdown: (id: string | null) => void;
  setActiveSlide: (index: number) => void;
  toggleAutoPlay: () => void;
  setSearchQuery: (query: string) => void;
  toggleSearch: () => void;
}

export const useUIStore = create<UIStore>((set) => ({
  // Initial state
  isMobileMenuOpen: false,
  activeDropdown: null,
  activeSlide: 0,
  isAutoPlaying: true,
  searchQuery: '',
  isSearchOpen: false,

  // Actions
  toggleMobileMenu: () =>
    set((state) => ({
      isMobileMenuOpen: !state.isMobileMenuOpen,
    })),

  setActiveDropdown: (id: string | null) =>
    set({
      activeDropdown: id,
    }),

  setActiveSlide: (index: number) =>
    set({
      activeSlide: index,
    }),

  toggleAutoPlay: () =>
    set((state) => ({
      isAutoPlaying: !state.isAutoPlaying,
    })),

  setSearchQuery: (query: string) =>
    set({
      searchQuery: query,
    }),

  toggleSearch: () =>
    set((state) => ({
      isSearchOpen: !state.isSearchOpen,
    })),
}));
